// This text appears instantly
function hello() {
  console.log('Hello, World!');
}
hello();

// This text appears character by character
function goodbye() {
  console.log('Goodbye, World!');
}
goodbye();

// This text appears in chunks like hackertyper.net
function hackerTyper() {
  const skills = ['HTML', 'CSS', 'JavaScript', 'TypeScript', 'React', 'Node.js'];

  skills.forEach((skill, index) => {
    console.log(`Skill ${index + 1}: ${skill}`);
  });

  return 'I am a coding wizard! âœ¨';
}

console.log(hackerTyper());

// Larger chunks (5 characters per keystroke)
class HackerDemo {
  constructor(name) {
    this.name = name;
    this.isAwesome = true;
  }

  showOff() {
    return `${this.name} is writing code at lightning speed!`;
  }
}

const demo = new HackerDemo('Demo Time User');
console.log(demo.showOff());
